function drawLines(points,mult,number){for(let n=0;n<ITERATIONS;n++){let pairPoint,pairPointIndex=n*mult%number,currentPointIndex=n%number;line(points[currentPointIndex].x,points[currentPointIndex].y,points[pairPointIndex].x,points[pairPointIndex].y)}}function drawScene(points,x,y,r,shownumbers){circle(x,y,2*r);for(let p in points){let po=points[p];shownumbers&&text(p,po.tx,po.ty)}}function getAnchorPointCoordinates(x,y,r,number){const ANG_DELTA=2*Math.PI/number,TEXT_R_DELTA=25;let points={},n=0;for(let ang=0;ang<2*Math.PI-.005;ang+=ANG_DELTA){let xn=x+r*Math.cos(ang),yn=y+r*Math.sin(ang),xnt=Math.round(x+(r+25)*Math.cos(ang)),ynt=Math.round(y+(r+25)*Math.sin(ang));points[n]={x:xn,y:yn,tx:xnt,ty:ynt},n++}return points}function getRndColor(){return color(random(0,255),random(0,255),random(0,255))}